import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventForm = ({ fetchEvents }) => {
    const [title, setTitle] = useState('');
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [events, setEvents] = useState([]);

    // イベントを取得する
    useEffect(() => {
        fetchEvents();
    }, []);

    // イベント追加
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const newEvent = { title, start, end };
            await axios.post('http://localhost:5003/api/events', newEvent);

            // 入力フィールドをクリア
            setTitle('');
            setStart('');
            setEnd('');

            // イベントリストを更新
            fetchEvents();
        } catch (error) {
            console.log('Error adding event:', error);
        }
    };

    // イベント削除
    const handleDelete = async (eventId) => {
        try {
            await axios.delete(`http://localhost:5003/api/events/${eventId}`);
            console.log('Event deleted:', eventId);
            fetchEvents(); // 削除後、イベントリストを再取得
        } catch (error) {
            console.log('Error deleting event:', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <h3>予定を追加</h3>
                <div>
                    <label>タイトル：</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
                </div>
                <div>
                    <label>開始時刻：</label>
                    <input type="datetime-local" value={start} onChange={(e) => setStart(e.target.value)} required />
                </div>
                <div>
                    <label>終了時間：</label>
                    <input type="datetime-local" value={end} onChange={(e) => setEnd(e.target.value)} required />
                </div>
                <button type="submit">追加</button>
            </form>

            {/* 追加されたイベントの表示と削除ボタン */}
            <h3>予定リスト</h3>
            <ul>
                {events.map((event) => (
                    <li key={event._id}>
                        {event.title} ({event.start} - {event.end})
                        <button onClick={() => handleDelete(event._id)}>削除</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default EventForm;
