import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import axios from 'axios';
import EventForm from './EventForm';
import Modal from './Modal'
import { deleteEvent } from '../api';

const Calendar = () => {
  const [events, setEvents] = useState([]);
  const [modalOpen, setModalOpen] = useState(false); // „É¢„Éº„ÉÄ„É´„ÅÆÈñãÈñâÁä∂ÊÖã
  const [selectedEvent, setSelectedEvent] = useState(null); // „ÇØ„É™„ÉÉ„ÇØ„Åï„Çå„Åü‰∫àÂÆö„ÅÆË©≥Á¥∞

  // „Ç§„Éô„É≥„Éà„Çí„Éê„ÉÉ„ÇØ„Ç®„É≥„Éâ„Åã„ÇâÂèñÂæó
  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5003/api/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  // Êó•‰ªò„ÇØ„É™„ÉÉ„ÇØÊôÇ„ÅÆÂá¶ÁêÜÔºà‰∫àÂÆö„ÇíËøΩÂä†Ôºâ
  const handleDateClick = async (arg) => {
    if (modalOpen){
      return;
    } else {
      const eventTitle = prompt('‰∫àÂÆö„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      if (!eventTitle) return;
    }

    const newEvent = {
      title: eventTitle,
      start: arg.dateStr,
      end: arg.dateStr,
      description: '',
      location: '',
    };

    try {
      const response = await axios.post('http://localhost:5003/api/events', newEvent);
      console.log("ËøΩÂä†„Åï„Çå„Åü„Ç§„Éô„É≥„Éà:", response.data); // „Éá„Éê„ÉÉ„Ç∞Áî®
      setEvents([...events, response.data]); // ËøΩÂä†„Åï„Çå„Åü„Ç§„Éô„É≥„Éà„ÇíÂèçÊò†
    } catch (error) {
      console.error('Error adding event:', error);
    }
  };

  // ‰∫àÂÆö„Åå„ÇØ„É™„ÉÉ„ÇØ„Åï„Çå„Åü„Å®„Åç„Å´Ë©≥Á¥∞„ÇíÂèñÂæó„Åó„Å¶„É¢„Éº„ÉÄ„É´„ÇíÈñã„Åè
  const handleEventClick = (clickInfo) => {
    const eventDetails = {
      _id: clickInfo.event._def.extendedProps._id,
      title: clickInfo.event.title,
      start: clickInfo.event.start,
      end: clickInfo.event.end,
      description: clickInfo.event.extendedProps.description,
      location: clickInfo.event.extendedProps.location,
    };

    console.log('Event details for modal:', eventDetails); // „É¢„Éº„ÉÄ„É´„Å´Ê∏°„ÅôÂâç„Å´Á¢∫Ë™ç

    setSelectedEvent(eventDetails);  // eventDetails „Çí setSelectedEvent „Å´„Çª„ÉÉ„Éà
    setModalOpen(true);  // „É¢„Éº„ÉÄ„É´„ÇíÈñã„Åè
  };

  // „É¢„Éº„ÉÄ„É´„ÇíÈñâ„Åò„ÇãÂá¶ÁêÜ
  const closeModal = () => {
    setModalOpen(false); // „É¢„Éº„ÉÄ„É´„ÇíÈñâ„Åò„Çã
  };

  // „Ç§„Éô„É≥„Éà„ÅÆÂâäÈô§Âá¶ÁêÜ
  const handleEventDelete = async () => {
    if (!selectedEvent || !selectedEvent._id) {
      console.error('Event details are missing or incomplete');
      return;
    }

    try {
      await deleteEvent(selectedEvent._id);
      console.log('‚úÖ Event deleted:', selectedEvent._id);

      // üîπ MongoDB „ÅÆ„Éá„Éº„ÇøÂâäÈô§„ÅåÂÆå‰∫Ü„Åó„Å¶„Åã„Çâ„Ç§„Éô„É≥„Éà„É™„Çπ„Éà„ÇíÊõ¥Êñ∞
      setEvents(prevEvents => prevEvents.filter(event => event._id !== selectedEvent._id));

      setModalOpen(false); // „É¢„Éº„ÉÄ„É´„ÇíÈñâ„Åò„Çã
    } catch (error) {
      console.error('‚ùå Error deleting event:', error);
    }
};


  return (
    <div>
      <h2>My Calendar</h2>
      {/* ‰∫àÂÆöËøΩÂä†„Éï„Ç©„Éº„É† */}
      <EventForm fetchEvents={fetchEvents} />

      <FullCalendar 
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        events={events}
        dateClick={handleDateClick}
        eventClick={handleEventClick}  // „Ç§„Éô„É≥„Éà„ÇØ„É™„ÉÉ„ÇØÊôÇ„ÅÆÂá¶ÁêÜ
        eventTimeFormat={{
          hour: '2-digit',
          minute: '2-digit',
          hour12: false
        }}
      />

      {/* „É¢„Éº„ÉÄ„É´„ÇíË°®Á§∫ */}
      {modalOpen && (
        <Modal 
          isOpen={modalOpen}
          eventDetails={selectedEvent} // „É¢„Éº„ÉÄ„É´„Å´Ê∏°„Åô„Ç§„Éô„É≥„Éà„ÅÆË©≥Á¥∞
          onClose={closeModal} // „É¢„Éº„ÉÄ„É´„ÇíÈñâ„Åò„ÇãÈñ¢Êï∞
          onDelete={handleEventDelete} // ÂâäÈô§Èñ¢Êï∞„ÇíÊ∏°„Åô
        />
      )}
    </div>
  );
};

export default Calendar;
