import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import axios from 'axios';
import EventFormModal from './EventFormModal';
import Modal from './Modal'
import { deleteEvent } from '../api';

const Calendar = () => {
  const [events, setEvents] = useState([]);
  const [modalOpen, setModalOpen] = useState(false); // „É¢„Éº„ÉÄ„É´„ÅÆÈñãÈñâÁä∂ÊÖã
  const [selectedEvent, setSelectedEvent] = useState(null); // „ÇØ„É™„ÉÉ„ÇØ„Åï„Çå„Åü‰∫àÂÆö„ÅÆË©≥Á¥∞
  const [formModalOpen, setFormModalOpen] = useState(false);

  // „Ç§„Éô„É≥„Éà„Çí„Éê„ÉÉ„ÇØ„Ç®„É≥„Éâ„Åã„ÇâÂèñÂæó
  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5003/api/events');

      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const openFormModal = () => setFormModalOpen(true);
  const closeFormModal = () => setFormModalOpen(false);

  // ‰∫àÂÆö„Åå„ÇØ„É™„ÉÉ„ÇØ„Åï„Çå„Åü„Å®„Åç„Å´Ë©≥Á¥∞„ÇíÂèñÂæó„Åó„Å¶„É¢„Éº„ÉÄ„É´„ÇíÈñã„Åè
  const handleEventClick = (clickInfo) => {
    const eventDetails = {
      _id: clickInfo.event._def.extendedProps._id,
      title: clickInfo.event.title,
      start: clickInfo.event.start,
      end: clickInfo.event.end,
      description: clickInfo.event.extendedProps.description || "",
      location: clickInfo.event.extendedProps.location || "",
    };

    console.log('Event details for modal:', eventDetails); // „É¢„Éº„ÉÄ„É´„Å´Ê∏°„ÅôÂâç„Å´Á¢∫Ë™ç

    setSelectedEvent(eventDetails);  // eventDetails „Çí setSelectedEvent „Å´„Çª„ÉÉ„Éà
    setModalOpen(true);  // „É¢„Éº„ÉÄ„É´„ÇíÈñã„Åè
  };

  // „Ç§„Éô„É≥„Éà„ÅÆÂâäÈô§Âá¶ÁêÜ
  const handleEventDelete = async () => {
    if (!selectedEvent || !selectedEvent._id) {
      console.error('Event details are missing or incomplete');
      return;
    }

    try {
      await deleteEvent(selectedEvent._id);
      console.log('‚úÖ Event deleted:', selectedEvent._id);

      // üîπ MongoDB „ÅÆ„Éá„Éº„ÇøÂâäÈô§„ÅåÂÆå‰∫Ü„Åó„Å¶„Åã„Çâ„Ç§„Éô„É≥„Éà„É™„Çπ„Éà„ÇíÊõ¥Êñ∞
      setEvents(prevEvents => prevEvents.filter(event => event._id !== selectedEvent._id));

      setModalOpen(false); // „É¢„Éº„ÉÄ„É´„ÇíÈñâ„Åò„Çã
    } catch (error) {
      console.error('‚ùå Error deleting event:', error);
    }
};

return (
  <div className="calendar-container">
    <h2>My Calendar</h2>

    {/* ‰∫àÂÆö„ÇíËøΩÂä†„Éú„Çø„É≥ */}
    <button className="add-event-button" onClick={openFormModal}>‰∫àÂÆö„ÇíËøΩÂä†</button>

    <FullCalendar 
      plugins={[dayGridPlugin, interactionPlugin]}
      initialView="dayGridMonth"
      events={events}
      eventClick={handleEventClick}
      eventContent={(eventInfo) => (
        <div className="fc-event-title">
          {eventInfo.event.title}
        </div>
      )}
    />

    {/* ‰∫àÂÆöËøΩÂä†„É¢„Éº„ÉÄ„É´ */}
    {formModalOpen && <EventFormModal onClose={closeFormModal} fetchEvents={fetchEvents} />}

    {/* ‰∫àÂÆöË©≥Á¥∞„É¢„Éº„ÉÄ„É´ */}
    {modalOpen && (
      <Modal 
        isOpen={modalOpen}
        eventDetails={selectedEvent}
        onClose={() => setModalOpen(false)}
        onDelete={handleEventDelete}
      />
    )}
  </div>
);
};

export default Calendar;
