import React from 'react';

const Modal = ({ isOpen, eventDetails, onClose }) => {
    if (!isOpen) return null; // モーダルが開いていない場合は何も表示しない

    // Dateオブジェクトを文字列に変換
    const startDate = new Date(eventDetails.start).toLocaleString();
    const endDate = new Date(eventDetails.end).toLocaleString();

    return (
        <div style={styles.modal}>
            <div style={styles.modalContent}>
                <h3>{eventDetails.title}</h3>
                <p><strong>Start:</strong> {startDate}</p>
                <p><strong>End:</strong> {endDate}</p>
                <p><strong>Description</strong> {eventDetails.description || 'No description'}</p>
                <p><strong>Location:</strong> {eventDetails.location || 'No location'}</p>
                <button onClick={onClose} className='close-button'>Close</button>
            </div>
        </div>
    );
};

const styles = {
    modal: {
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    modalContent: {
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '5px',
        maxWidth: '400px',
        width: '100%',
    },
};

export default Modal;
