import React, { useState } from 'react';
import axios from 'axios';
import '../EventForm.css';

const EventForm = ({ fetchEvents }) => {
    const [title, setTitle] = useState('');
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [description, setDescription] = useState('');
    const [location, setLocation] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const newEvent = { title, start, end ,description, location };
            await axios.post('http://localhost:5003/api/events', newEvent);

            setTitle('');
            setStart('');
            setEnd('');
            setDescription('');
            setLocation('');

            // 最初のイベントを再取得
            fetchEvents();
        } catch (error) {
            console.log('Error adding event:', error);
        }
    };

    return (
        <div className="event-form-container">
            <h3>予定を追加</h3>
            <form className="event-form" onSubmit={handleSubmit}>
                <input type="text" placeholder="タイトル" value={title} onChange={(e) => setTitle(e.target.value)} required/>
                <input type="datetime-local" value={start} onChange={(e) => setStart(e.target.value)} required/>
                <input type="datetime-local" value={end} onChange={(e) => setEnd(e.target.value)} required/>
                <textarea placeholder="説明（オプション）" value={description} onChange={(e) => setDescription(e.target.value)}/>
                <input type="text" placeholder="場所（オプション）" value={location} onChange={(e) => setLocation(e.target.value)} />
                <button type="submit">追加</button>
            </form>
        </div>
    );
};

export default EventForm;
