import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import axios from 'axios';
import EventForm from './EventForm';
import Modal from './Modal'
import { deleteEvent } from '../api';

const Calendar = () => {
  const [events, setEvents] = useState([]);
  const [modalOpen, setModalOpen] = useState(false); // モーダルの開閉状態
  const [selectedEvent, setSelectedEvent] = useState(null); // クリックされた予定の詳細

  // イベントをバックエンドから取得
  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  // 日付クリック時の処理（予定を追加）
  const handleDateClick = async (arg) => {
    if (modalOpen){
      return;
    } else {
      const eventTitle = prompt('予定を入力してください');
      if (!eventTitle) return;
    }

    const newEvent = {
      title: eventTitle,
      start: arg.dateStr,
      end: arg.dateStr,
      description: '',
      location: '',
    };

    try {
      const response = await axios.post('http://localhost:5001/api/events', newEvent);
      console.log("追加されたイベント:", response.data); // デバッグ用
      setEvents([...events, response.data]); // 追加されたイベントを反映
    } catch (error) {
      console.error('Error adding event:', error);
    }
  };

  // 予定がクリックされたときに詳細を取得してモーダルを開く
  const handleEventClick = (clickInfo) => {
    console.log('Clicked event:', clickInfo.event); // クリックされたイベントの内容を確認
    const eventDetails = {
      _id: clickInfo.event._def.publicId, // FullCalendarのイベントオブジェクトからidを取得
      title: clickInfo.event.title,
      start: clickInfo.event.start,
      end: clickInfo.event.end,
      description: clickInfo.event.extendedProps.description,
      location: clickInfo.event.extendedProps.location,
    };
    console.log('Event details for modal:', eventDetails);  // ここで渡す前のデータを確認
    setSelectedEvent(eventDetails); // 修正したイベントデータをセット
    setModalOpen(true);
  };

  // モーダルを閉じる処理
  const closeModal = () => {
    setModalOpen(false); // モーダルを閉じる
  };

  // イベントの削除処理
  const handleDeleteEvent = async () => {
    try {
      // バックエンドに削除リクエストを送信
      //await axios.delete(`http://localhost:5001/api/events/${eventId}`);

      await deleteEvent(eventDetails.d);

      // ローカルのイベントリストから削除
      setEvents(events.filter(event => event._id !== eventId));
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  return (
    <div>
      <h2>My Calendar</h2>
      {/* 予定追加フォーム */}
      <EventForm fetchEvents={fetchEvents} />

      <FullCalendar 
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        events={events}
        dateClick={handleDateClick}
        eventClick={handleEventClick}  // イベントクリック時の処理
        eventTimeFormat={{
          hour: '2-digit',
          minute: '2-digit',
          hour12: false
        }}
      />

      {/* モーダルを表示 */}
      <Modal 
        isOpen={modalOpen}
        eventDetails={selectedEvent || {}} // モーダルに渡すイベントの詳細
        onClose={closeModal} // モーダルを閉じる関数
        onDelete={handleDeleteEvent} // 削除関数を渡す
      />
    </div>
  );
};

export default Calendar;
